-- ----------------------------
DROP TABLE IF EXISTS `wg_emergency`;
CREATE TABLE `wg_emergency` (
  `rid` int(11) NOT NULL AUTO_INCREMENT,
  `domainId` int(11) NOT NULL,
  `clientId` int(11) NOT NULL,
  `category` char(200) NOT NULL,
  `description` varchar(2000) DEFAULT NULL,
  `address` varchar(500) DEFAULT NULL,
  `lat` char(20) DEFAULT NULL,
  `lng` char(20) DEFAULT NULL,
  `images` varchar(5000) DEFAULT NULL,
  `video` varchar(5000) DEFAULT NULL,
  `mainImage` char(255) DEFAULT NULL,
  `creDate` datetime DEFAULT NULL,
  `status` char(1) DEFAULT NULL COMMENT 'T:临时 C:完成',
  `type` tinyint(1) DEFAULT '1' COMMENT '1：有重大事件发生 0:无重大事件发生 -1:需要修改的重大事件',
  `updateDate` datetime DEFAULT NULL,
  `updateId` int(11) DEFAULT NULL,
  `contactMan` char(20) DEFAULT NULL,
  `mobile` char(13) DEFAULT NULL,
  PRIMARY KEY (`rid`),
  KEY `creDate_type` (`creDate`,`type`),
  KEY `category_creDate_index` (`domainId`,`category`,`creDate`),
  KEY `client_type` (`clientId`,`type`,`creDate`),
  KEY `domain_type` (`domainId`,`clientId`,`creDate`,`type`)
) ENGINE=InnoDB AUTO_INCREMENT=168 DEFAULT CHARSET=utf8;

