INSERT INTO `wg_news` VALUES ('45', '1', '1', '福田区2015上半年开展选配楼栋长工作取得好成绩', '/repository/20151021/e665f85eb3717969c3142b751d8d2672_.jpg', null, '2015-10-21 20:40:56', '南园街道', '<p style=\"text-align: center;\"><img src=\"/editor/656812216024174592.png\" title=\"\" alt=\"141030094636666.jpg\"/></p><section class=\"135editor\" data-id=\"62942\" style=\"border: 0px none; padding: 0px;\" data-color=\"rgb(30, 155, 232)\" data-custom=\"rgb(30, 155, 232)\"><section style=\"width: 100%; border-color: rgb(30, 155, 232) rgb(198, 198, 198) rgb(198, 198, 198); border-style: solid; border-width: 8px 1px 1px; margin-top: 0px; padding: 15px 10px 5px; text-align: center;\" data-width=\"100%\"><section class=\"135brush\" data-style=\"text-align: center; line-height:2em;\" style=\"margin:5px;padding:5px;\"><p style=\"text-align: left; text-indent: 0em;\">我区共有符合选配楼栋长标准的住宅类楼宇12473栋，已选配9924名楼长，实配楼栋数为12368栋，选配率达99.2%。全区共更新楼长人选519名。全区楼栋长及其协会组织共向街道综管所、社区综管站报送人口信息4.7万条，出租屋安全隐患信息3418条，协助相关部门处理隐患信息1917条，进一步强化了信息登记、治安防控和日常管理工作。全区共发展楼长会员449名，建立7个社区楼长服务站和3个社区楼长活动室，街道楼长协会和社区楼长服务站为加强楼长管理、弘扬楼长文化起到了积极作用。</p><p style=\"text-align: right;\"><span style=\"font-size: 12px;\">（租赁局：赵玲）</span></p></section></section></section><p><br/></p>');
INSERT INTO `wg_news` VALUES ('46', '1', '1', '“三方面”创新楼长业务培训内容', '/repository/20151021/b3288f4071c90966f07145e991f030bc_.jpg', null, '2015-10-21 20:46:08', '南园街道', '<p style=\"text-align: center;\"><img src=\"/editor/656813562001494016.png\" title=\"\" alt=\"“三方面”创新楼长业务培训内容.jpg\"/></p><section class=\"135editor\" data-id=\"62942\" style=\"border: 0px none; padding: 0px;\" data-color=\"rgb(30, 155, 232)\" data-custom=\"rgb(30, 155, 232)\"><section style=\"width: 100%; border-color: rgb(30, 155, 232) rgb(198, 198, 198) rgb(198, 198, 198); border-style: solid; border-width: 8px 1px 1px; margin-top: 0px; padding: 15px 10px 5px; text-align: center;\" data-width=\"100%\"><section class=\"135brush\" data-style=\"text-align: center; line-height:2em;\" style=\"margin:5px;padding:5px;\"><p style=\"text-align: left; text-indent: 0em;\">今年，梅林街道楼长业务培训结合实际情况，创新培训内容，拟定了相关三个方面内容。一是明确楼长的工作职责，密切配合社区综管员（网格员）开展相关的服务工作，即楼长“一个宣传、两个报告、三个协助”，当好房管系统的“义工”。二是认清当前深圳（特别是自己居住环境）的社会治安形势，协同有关部门做好群防群治工作，做好防邪反邪、禁毒、反恐的宣传教育。三是针对楼长在日常的服务工作中如何应对遇到相关具体情况，把楼长在实际工作中所遇到的具体情况，归纳为《楼长的十个怎么办》。</p><p style=\"text-align: right; text-indent: 2em;\"><span style=\"font-size: 12px;\">（信息员：蒋正兴、李念&nbsp;）</span></p></section></section></section><p><br/></p>');
INSERT INTO `wg_news` VALUES ('47', '1', '1', '综管办四个专项行动重拳出击综合治理', '/repository/20151021/5d1f652bc6907f3917a654f6384630c7_.jpg', null, '2015-10-21 20:57:53', '南园街道', '<p style=\"text-align: center;\"><img src=\"/editor/656816611788984320.png\" title=\"\" alt=\"综管办四个专项行动重拳出击综合治理.jpg\"/></p><section class=\"135editor\" data-id=\"62942\" style=\"border: 0px none; padding: 0px;\" data-color=\"rgb(30, 155, 232)\" data-custom=\"rgb(30, 155, 232)\"><section style=\"width: 100%; border-color: rgb(30, 155, 232) rgb(198, 198, 198) rgb(198, 198, 198); border-style: solid; border-width: 8px 1px 1px; margin-top: 0px; padding: 15px 10px 5px; text-align: center;\" data-width=\"100%\"><section class=\"135brush\" data-style=\"text-align: center; line-height:2em;\" style=\"margin:5px;padding:5px;\"><p style=\"text-align: left; text-indent: 0em;\">上半年，我办加强信息排查力度，展开四次专项行动，综合治理成效显著。</p><p><br/></p><section class=\"135editor\" data-id=\"41173\" style=\"border: 0px none; padding: 0px;\" data-color=\"rgb(30, 155, 232)\" data-custom=\"rgb(30, 155, 232)\"><h2 style=\"margin: 8px 0px 0px; padding: 0px; font-size: 16px; line-height: 28px; max-width: 100%; color: rgb(30, 155, 232); min-height: 32px; border-bottom-width: 1.5px; border-bottom-style: solid; border-color: rgb(30, 155, 232); text-align: justify;\"><span class=\"autonum\" data-original-title=\"\" placeholder=\"1\" style=\"background-color: rgb(30, 155, 232); color: rgb(255, 255, 255); display: block; float: left; line-height: 20px; margin: 0px 8px 0px 0px; max-width: 100%; padding: 4px 10px; word-wrap: break-word !important;\" title=\"\">1</span><strong class=\"135brush\" data-brushtype=\"text\" style=\"border-color: rgb(30, 155, 232); color: inherit;\">信息采集岁末年初专项行动</strong></h2><section style=\"width: 0px; height: 0px; clear: both;\"></section></section><p><br/></p><p style=\"text-align: left; text-indent: 0em;\"><span style=\"line-height: inherit;\">1月至3月，我办联合区公安分局加强宣传发动，共张贴告示8499张，派发宣传单张10万份，通过媒体宣传427次；组织信息采集，清查出租屋12.1万套（间），核查流动人口22万余人，新登记人口信息51535条，注销43660条；协助处理消防隐患、治安隐患、无证经营、计生问题及其他隐患合计1612宗。</span></p><p style=\"text-align: left; text-indent: 0em;\"><br/></p><section class=\"135editor\" data-id=\"41173\" style=\"border: 0px none; padding: 0px;\" data-color=\"rgb(30, 155, 232)\" data-custom=\"rgb(30, 155, 232)\"><h2 style=\"margin: 8px 0px 0px; padding: 0px; font-size: 16px; line-height: 28px; max-width: 100%; color: rgb(30, 155, 232); min-height: 32px; border-bottom-width: 1.5px; border-bottom-style: solid; border-color: rgb(30, 155, 232); text-align: justify;\"><span class=\"autonum\" data-original-title=\"\" placeholder=\"1\" style=\"background-color: rgb(30, 155, 232); color: rgb(255, 255, 255); display: block; float: left; line-height: 20px; margin: 0px 8px 0px 0px; max-width: 100%; padding: 4px 10px; word-wrap: break-word !important;\" title=\"\">2</span><strong class=\"135brush\" data-brushtype=\"text\" style=\"border-color: rgb(30, 155, 232); color: inherit;\">“四实”信息采集春季专项行动</strong></h2><section style=\"width: 0px; height: 0px; clear: both;\"></section></section><p><br/></p><p style=\"text-align: left; text-indent: 0em;\"><span style=\"line-height: inherit;\">3月到5月,逐一核查、采集城中村、老旧住宅小区、高层楼宇等“四有”信息。各街道累计集中开展行动434次，共采集实有人口信息212030条，注销165576条；采集实有法人（机构）16505条，注销955条；实有房屋地图比对159523处，采集73522处，注销1321处；实有事件信息采集排查共8137宗，清查房屋345340套。</span></p><p style=\"text-align: left; text-indent: 2em;\"><br/></p><section class=\"135editor\" data-id=\"41173\" style=\"border: 0px none; padding: 0px;\" data-color=\"rgb(30, 155, 232)\" data-custom=\"rgb(30, 155, 232)\"><h2 style=\"margin: 8px 0px 0px; padding: 0px; font-size: 16px; line-height: 28px; max-width: 100%; color: rgb(30, 155, 232); min-height: 32px; border-bottom-width: 1.5px; border-bottom-style: solid; border-color: rgb(30, 155, 232); text-align: justify;\"><span class=\"autonum\" data-original-title=\"\" placeholder=\"1\" style=\"background-color: rgb(30, 155, 232); color: rgb(255, 255, 255); display: block; float: left; line-height: 20px; margin: 0px 8px 0px 0px; max-width: 100%; padding: 4px 10px; word-wrap: break-word !important;\" title=\"\">3</span><strong class=\"135brush\" data-brushtype=\"text\" style=\"border-color: rgb(30, 155, 232); color: inherit;\">青少年重点人群排查和网吧信息排查专项行动</strong></h2><section style=\"width: 0px; height: 0px; clear: both;\"></section></section><p><br/></p><p style=\"text-align: left; text-indent: 0em;\"><span style=\"line-height: inherit;\">4月，我办组织全区网格员对辖区房屋及实有人口进行“拉网式”排查，并作为实有法人信息全部录入到全市统一的信息采集系统。共登记网吧信息142条，排查出无证经营网吧57家，已通过福田区社会管理工作信息网向有关部门进行了通报。</span></p><p style=\"text-align: left; text-indent: 2em;\"><br/></p><section class=\"135editor\" data-id=\"41173\" style=\"border: 0px none; padding: 0px;\" data-color=\"rgb(30, 155, 232)\" data-custom=\"rgb(30, 155, 232)\"><h2 style=\"margin: 8px 0px 0px; padding: 0px; font-size: 16px; line-height: 28px; max-width: 100%; color: rgb(30, 155, 232); min-height: 32px; border-bottom-width: 1.5px; border-bottom-style: solid; border-color: rgb(30, 155, 232); text-align: justify;\"><span class=\"autonum\" data-original-title=\"\" placeholder=\"1\" style=\"background-color: rgb(30, 155, 232); color: rgb(255, 255, 255); display: block; float: left; line-height: 20px; margin: 0px 8px 0px 0px; max-width: 100%; padding: 4px 10px; word-wrap: break-word !important;\" title=\"\">4</span><strong class=\"135brush\" data-brushtype=\"text\" style=\"border-color: rgb(30, 155, 232); color: inherit;\">严打整治暴恐活动专项行动</strong></h2><section style=\"width: 0px; height: 0px; clear: both;\"></section></section><p><br/></p><p style=\"text-align: left; text-indent: 0em;\"><span style=\"line-height: inherit;\"></span><span style=\"line-height: inherit;\">5月中旬，加强对辖区内重点工作对象的信息采集、核查和注销工作，全区排查“特情”隐患人员信息2124条；6月下旬，下发58000份《出租屋流动人口综合管理责任书》，组织各街道综治办与出租屋综管所签订，推动了房屋出租人依法申报居住登记，强化了各项反恐措施。</span></p><p style=\"text-align: right;\"><span style=\"font-size: 12px;\">（租赁局：李念）</span></p></section></section></section><p><br/></p>');
INSERT INTO `wg_news` VALUES ('48', '1', '1', '房屋租赁业务座谈会提新要求', '/nanyuan/repository/20151203/78a7234db5c1913424be912ae2468af5_.jpg', null, '2015-10-21 21:13:05', '福田综管', '<p><img src=\"/editor/656820374247444480.png\" title=\"\" alt=\"房屋租赁业务座谈会提新要求.jpg\"/></p><section class=\"135editor\" data-id=\"62942\" style=\"border: 0px none; padding: 0px;\" data-color=\"rgb(30, 155, 232)\" data-custom=\"rgb(30, 155, 232)\"><section style=\"width: 100%; border-color: rgb(30, 155, 232) rgb(198, 198, 198) rgb(198, 198, 198); border-style: solid; border-width: 8px 1px 1px; margin-top: 0px; padding: 15px 10px 5px; text-align: center;\" data-width=\"100%\"><section class=\"135brush\" data-style=\"text-align: center; line-height:2em;\" style=\"margin:5px;padding:5px;\"><p style=\"text-align: left;\">近日，福田租赁所举行落实“三严三实”促企业发展座谈会，深圳市房屋租赁行业协会、市租赁办、福田区租赁局、福田租赁所以及福田租赁所辖区的37家会员公司等负责人参加了会议，会议对相关责任主体提出新要求：</p><p><br/></p><section class=\"135editor\" data-id=\"45\" style=\"border: 0px none; padding: 0px;\" data-color=\"rgb(30, 155, 232)\" data-custom=\"rgb(30, 155, 232)\"><section style=\"margin: 0.8em 0px 0.5em; line-height: 32px; font-weight: bold;\"><section style=\"display: inline-block; float: left; width: 32px; height: 32px; border-top-left-radius: 32px; border-top-right-radius: 32px; border-bottom-right-radius: 32px; border-bottom-left-radius: 32px; vertical-align: top; background-color: rgb(30, 155, 232); border-color: rgb(30, 155, 232); color: rgb(255, 255, 255);\"><section style=\"display: table; width: 100%; color: inherit; border-color: rgb(30, 155, 232);\" data-width=\"100%\"><section class=\"autonum\" placeholder=\"1\" style=\"display: table-cell; vertical-align: middle; border-color: rgb(30, 155, 232);\" data-original-title=\"\" title=\"\">1</section></section></section><section class=\"135brush\" data-brushtype=\"text\" style=\"margin-left: 36px; color: rgb(30, 155, 232); border-color: rgb(30, 155, 232); text-align: left;\">要求租赁所严以用权，谋实事</section></section><section style=\"width: 0px; height: 0px; clear: both;\"></section></section><p><br/></p><p style=\"text-align: left;\"><span style=\"line-height: inherit;\">树立“没有权利，只有服务”的服务意识，在法规政策的框架下，改革创新，便民服务。</span></p><p><br/></p><section class=\"135editor\" data-id=\"45\" style=\"border: 0px none; padding: 0px;\" data-color=\"rgb(30, 155, 232)\" data-custom=\"rgb(30, 155, 232)\"><section style=\"margin: 0.8em 0px 0.5em; line-height: 32px; font-weight: bold;\"><section style=\"display: inline-block; float: left; width: 32px; height: 32px; border-top-left-radius: 32px; border-top-right-radius: 32px; border-bottom-right-radius: 32px; border-bottom-left-radius: 32px; vertical-align: top; background-color: rgb(30, 155, 232); border-color: rgb(30, 155, 232); color: rgb(255, 255, 255);\"><section style=\"display: table; width: 100%; color: inherit; border-color: rgb(30, 155, 232);\" data-width=\"100%\"><section class=\"autonum\" placeholder=\"1\" style=\"display: table-cell; vertical-align: middle; border-color: rgb(30, 155, 232);\" data-original-title=\"\" title=\"\">2</section></section></section><section class=\"135brush\" data-brushtype=\"text\" style=\"margin-left: 36px; color: rgb(30, 155, 232); border-color: rgb(30, 155, 232); text-align: left;\">要求房屋租赁行业的会员单位业务转型</section></section><section style=\"width: 0px; height: 0px; clear: both;\"></section></section><p><br/></p><p style=\"text-align: left;\"><span style=\"line-height: inherit;\">单一型转智能综合型，创“房屋租赁网+”模式，充分发挥互联网在生产要素配置中的优化和集成作用，集房屋租赁服务、资产运营、社区服务为一体；跨界经营，促进租赁价值的提升。</span></p><p style=\"text-align: right;\"><span style=\"font-size: 12px;\">(福田所：何健)</span></p></section></section></section><p><br/></p>');
INSERT INTO `wg_news` VALUES ('49', '1', '2', '“福田综管”微信平台快采快报操作培训圆满完成', '/repository/20151102/f0f77179fe8995005f13aa85be7ecc0b_.jpg', null, '2015-11-02 15:13:26', '南园街道', '<p><img src=\"/editor/661078363875708928.png\" style=\"\"/></p><p><img src=\"/editor/661078364668432384.png\" style=\"\"/></p><section class=\"135editor\" style=\"position: static; box-sizing: border-box; border: 0px none; padding: 0px; -webkit-transform: none;\" data-id=\"62942\" data-color=\"rgb(30, 155, 232)\" data-custom=\"rgb(30, 155, 232)\"><section style=\"width: 100%; border-color: rgb(30, 155, 232) rgb(198, 198, 198) rgb(198, 198, 198); border-style: solid; border-width: 8px 1px 1px; margin-top: 0px; padding: 15px 10px 5px; text-align: center;\" data-width=\"100%\"><section class=\"135brush\" data-style=\"text-align: center; line-height:2em;\" style=\"margin:5px;padding:5px;\"><p style=\"font-size: 15.555556297302246px; text-align: left;\">10月26日至30日，我办组织开展了十场“福田综管”微信平台快采快报操作培训班，全区近1500人参加了培训，每场培训结束后的现场测试结果显示，参训人员基本掌握了平台“快采快报”各项操作功能。</p><p style=\"font-size: 15.555556297302246px;\"><br/></p><section class=\"135editor\" style=\"position: static; box-sizing: border-box; border: 0px none; padding: 0px; -webkit-transform: none;\" data-id=\"32290\" data-color=\"rgb(30, 155, 232)\" data-custom=\"rgb(30, 155, 232)\"><section style=\"clear: both; padding: 0px; border: 0px none; margin:5px 0px;\"><section style=\"border-top-width: 2.5px; border-top-style: solid; border-color: rgb(30, 155, 232); font-size: 1em; font-weight: inherit; text-decoration: inherit; color: rgb(255, 255, 255); box-sizing: border-box;\"><section style=\"border: 0px rgb(30, 155, 232); margin: 2px 0px 0px; overflow: hidden; padding: 0px; color: inherit; text-align: left;\"><section style=\"display: inline-block; font-size: 1em; font-family: inherit; font-weight: inherit; text-align: inherit; text-decoration: inherit; color: inherit; border-color: rgb(30, 155, 232);\"><section class=\"135brush\" data-bcless=\"darken\" data-brushtype=\"text\" style=\"display: inline-block; line-height: 1.4em; padding: 5px 10px; height: 32px; vertical-align: top; font-family: inherit; font-weight: bold; float: left; color: inherit; border-color: rgb(15, 95, 145); background-color: rgb(30, 155, 232); box-sizing: border-box !important;\">（一）覆盖人员广</section><section style=\"display: inline-block; vertical-align: top; width: 0px; height: 0px; border-top-width: 32px; border-top-style: solid; border-top-color: rgb(30, 155, 232); border-right-width: 32px; border-right-style: solid; border-right-color: transparent; border-top-right-radius: 4px; border-bottom-left-radius: 2px; color: inherit; box-sizing: border-box !important;\"></section></section></section></section></section><section style=\"width: 0px; height: 0px; clear: both;\"></section></section><p><br/></p><p style=\"font-size: 15.555556297302246px; text-align: left;\"><span style=\"line-height: inherit;\">各街道综管所分管所长、指导检查组成员，街道、社区“快采快报”工作负责人（含工作联络人及社区公安兼职指导员）以及全体网格员近1500人参加培训。</span></p><p style=\"font-size: 15.555556297302246px; text-align: left;\"><br/></p><section class=\"135editor\" style=\"position: static; box-sizing: border-box; border: 0px none; padding: 0px; -webkit-transform: none;\" data-id=\"32290\" data-color=\"rgb(30, 155, 232)\" data-custom=\"rgb(30, 155, 232)\"><section style=\"clear: both; padding: 0px; border: 0px none; margin:5px 0px;\"><section style=\"border-top-width: 2.5px; border-top-style: solid; border-color: rgb(30, 155, 232); font-size: 1em; font-weight: inherit; text-decoration: inherit; color: rgb(255, 255, 255); box-sizing: border-box;\"><section style=\"border: 0px rgb(30, 155, 232); margin: 2px 0px 0px; overflow: hidden; padding: 0px; color: inherit; text-align: left;\"><section style=\"display: inline-block; font-size: 1em; font-family: inherit; font-weight: inherit; text-align: inherit; text-decoration: inherit; color: inherit; border-color: rgb(30, 155, 232);\"><section class=\"135brush\" data-bcless=\"darken\" data-brushtype=\"text\" style=\"display: inline-block; line-height: 1.4em; padding: 5px 10px; height: 32px; vertical-align: top; font-family: inherit; font-weight: bold; float: left; color: inherit; border-color: rgb(15, 95, 145); background-color: rgb(30, 155, 232); box-sizing: border-box !important;\">（二）注重实际操作</section><section style=\"display: inline-block; vertical-align: top; width: 0px; height: 0px; border-top-width: 32px; border-top-style: solid; border-top-color: rgb(30, 155, 232); border-right-width: 32px; border-right-style: solid; border-right-color: transparent; border-top-right-radius: 4px; border-bottom-left-radius: 2px; color: inherit; box-sizing: border-box !important;\"></section></section></section></section></section><section style=\"width: 0px; height: 0px; clear: both;\"></section></section><p><br/></p><p style=\"font-size: 15.555556297302246px; text-align: left;\"><span style=\"line-height: inherit;\">参训人员通过绑定PDA采集终端工作号，技术开发人员针对各个模块的使用进行详细讲解，模拟场景操作，进行现场各层级权限人员的操作测试，确保参训人员知晓“快采快报”各环节的关联关系与操作流程。</span></p><p style=\"font-size: 15.555556297302246px; text-align: left;\"><br/></p><section class=\"135editor\" style=\"position: static; box-sizing: border-box; border: 0px none; padding: 0px; -webkit-transform: none;\" data-id=\"32290\" data-color=\"rgb(30, 155, 232)\" data-custom=\"rgb(30, 155, 232)\"><section style=\"clear: both; padding: 0px; border: 0px none; margin:5px 0px;\"><section style=\"border-top-width: 2.5px; border-top-style: solid; border-color: rgb(30, 155, 232); font-size: 1em; font-weight: inherit; text-decoration: inherit; color: rgb(255, 255, 255); box-sizing: border-box;\"><section style=\"border: 0px rgb(30, 155, 232); margin: 2px 0px 0px; overflow: hidden; padding: 0px; color: inherit; text-align: left;\"><section style=\"display: inline-block; font-size: 1em; font-family: inherit; font-weight: inherit; text-align: inherit; text-decoration: inherit; color: inherit; border-color: rgb(30, 155, 232);\"><section class=\"135brush\" data-bcless=\"darken\" data-brushtype=\"text\" style=\"display: inline-block; line-height: 1.4em; padding: 5px 10px; height: 32px; vertical-align: top; font-family: inherit; font-weight: bold; float: left; color: inherit; border-color: rgb(15, 95, 145); background-color: rgb(30, 155, 232); box-sizing: border-box !important;\">（三）培训效果明显</section><section style=\"display: inline-block; vertical-align: top; width: 0px; height: 0px; border-top-width: 32px; border-top-style: solid; border-top-color: rgb(30, 155, 232); border-right-width: 32px; border-right-style: solid; border-right-color: transparent; border-top-right-radius: 4px; border-bottom-left-radius: 2px; color: inherit; box-sizing: border-box !important;\"></section></section></section></section></section><section style=\"width: 0px; height: 0px; clear: both;\"></section></section><p><br/></p><p style=\"font-size: 15.555556297302246px; text-align: left;\"><span style=\"line-height: inherit;\">每场培训预留30分钟互动环节，参训人员现场提问。对参训人员提出的各类问题，技术人员和平台设计人员都给予了一一解答。同时，培训方还为参训人员人手发放一册《“福田综管”微信公众平台用户手册》，方便网格员在实际工作中遇到问题时随机查阅解疑。</span></p><p style=\"font-size: 15.555556297302246px; text-align: right;\"><span style=\"line-height: inherit; font-size: 12px;\">(唐玉宏、李念、裴思晨)</span></p></section></section></section><p><br/></p>');
INSERT INTO `wg_news` VALUES ('50', '1', '2', '社区网格重特大信息实行“福田综管”微信平台快采快报', '/repository/20151102/5d9d017f2757c8b934303902eab520b7_.jpg', null, '2015-11-02 15:26:01', '南园街道', '<p><img src=\"/editor/661081726327590912.png\" title=\"\" alt=\"394002610541011516.jpg\"/></p><section class=\"135editor\" style=\"position: static; box-sizing: border-box; border: 0px none; padding: 0px; -webkit-transform: none;\" data-id=\"62942\" data-color=\"rgb(30, 155, 232)\" data-custom=\"rgb(30, 155, 232)\"><section style=\"width: 100%; border-color: rgb(30, 155, 232) rgb(198, 198, 198) rgb(198, 198, 198); border-style: solid; border-width: 8px 1px 1px; margin-top: 0px; padding: 15px 10px 5px; text-align: center;\" data-width=\"100%\"><section class=\"135brush\" data-style=\"text-align: center; line-height:2em;\" style=\"margin:5px;padding:5px;\"><p style=\"font-size: 15.555556297302246px; text-align: left;\">“福田综管”微信平台于11月1日正式上线运行。该平台作为福田区社区网格重特大信息快采快报渠道，遵守“眼见为实”原则，社区网格员通过PDA工作终端可随时随地将身边可能发生或正在发生的重特大信息通过文字、照片、视频等方式逐级上报，实现网格员从采集上报到区网格办接收信息流转时长最长不得超过3小时快采快报的时限要求，为区领导掌握基层特情和相关职能部门的应急处置提供了时间优先和信息来源的基本保障。<br/></p><p style=\"font-size: 15.555556297302246px; text-align: right;\"><span style=\"font-size: 12px;\">(唐玉宏、李念)</span></p></section></section></section><p><br/></p>');
