INSERT INTO `wg_people` VALUES ('8', '1', '1', '群众', '王慧容', '女', '/repository/20151023/c75a5ce51a2184f242fc914320348f72_.jpg', null, '香蜜湖街道东海社区', '<section class=\"135editor\" data-id=\"85605\" style=\"border: 0px none; padding: 0px;\" data-color=\"rgb(182, 228, 253)\" data-custom=\"rgb(182, 228, 253)\"><section style=\"margin: 10px 0px; padding: 0px; border: 0px rgb(182, 228, 253); width: 100%; box-sizing: border-box; color: rgb(62, 62, 62); border-top-left-radius: 4px; border-top-right-radius: 4px; border-bottom-right-radius: 4px; border-bottom-left-radius: 4px;\" data-width=\"100%\"><section style=\"box-sizing: border-box; padding: 10px 5px 8px; font-size: 14px; margin: 0px; color: inherit; background-color: rgb(182, 228, 253); border-top-left-radius: 4px; border-top-right-radius: 4px; border-bottom-right-radius: 4px; border-bottom-left-radius: 4px;\"><section class=\"135editor\" data-id=\"2266\" style=\"border: 0px none; padding: 0px; box-sizing: border-box; margin: 0px; font-family: 微软雅黑; color: inherit; border-top-left-radius: 4px; border-top-right-radius: 4px; border-bottom-right-radius: 4px; border-bottom-left-radius: 4px;\"><section style=\"border: 0px; box-sizing: border-box; width: 100%; clear: both; padding: 5px 0px 2px; margin: 0px; color: inherit; display: inline-block; border-top-left-radius: 4px; border-top-right-radius: 4px; border-bottom-right-radius: 4px; border-bottom-left-radius: 4px;\" data-width=\"100%\"><section style=\"box-sizing: border-box; width: 40%; float: right; padding: 0px 0px 0px 0.1em; text-align: right; margin: 0px; color: inherit; border-top-left-radius: 4px; border-top-right-radius: 4px; border-bottom-right-radius: 4px; border-bottom-left-radius: 4px;\" data-width=\"40%\"><img src=\"https://mmbiz.qlogo.cn/mmbiz/tf1wyeye4ekvRsRajefXpibURVJe0iaI3IP9dp048sONatM66GopOaSCK6JYGAfuF0ACus3dtibrQ0ru5IgXgHHng/0?wx_fmt=jpeg\" style=\"box-sizing: border-box; color: inherit; margin: 0px; padding: 0px; width: 90%; border-top-left-radius: 4px; border-top-right-radius: 4px; border-bottom-right-radius: 4px; border-bottom-left-radius: 4px; height: auto !important;\" data-width=\"90%\" width=\"90%\" height=\"auto\" border=\"0\" opacity=\"\" mapurl=\"\" title=\"\" alt=\"\"/></section><br/></section><section class=\"135brush\" data-brushtype=\"text\" style=\"border-top-left-radius: 4px; border-top-right-radius: 4px; border-bottom-right-radius: 4px; border-bottom-left-radius: 4px;\"><section style=\"margin: 4px 0px 6px; box-sizing: border-box; padding: 0px; color: inherit; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(254, 254, 254); border-top-left-radius: 4px; border-top-right-radius: 4px; border-bottom-right-radius: 4px; border-bottom-left-radius: 4px;\"><p><span style=\"font-size: 24px;\"><strong><span style=\"color: rgb(0, 0, 0); box-sizing: border-box; margin: 0px; padding: 0px; border-top-left-radius: 4px; border-top-right-radius: 4px; border-bottom-right-radius: 4px; border-bottom-left-radius: 4px;\">王慧容</span></strong></span>（香蜜湖街道东海社区网格员）</p></section></section></section><p><br/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">香蜜湖街道东海社区网格员王慧容，于10月19日在香珠花园核实、登记实有人口信息时，发现B栋810房屋因产权问题有纠纷发生，下午14时25分她再经过此处时，发现房屋外有两位老人被驱赶于门外，不允许进门，王慧容心生怜惜，尽力安慰两老，并立即报警，目前，警方已介入要求双方通过司法程序解决。王慧容的善良给了两老温暖，她的工作态度更是值得我们学习。</span></p><section style=\"width: 100%; border-width: 2px 0px 8px; border-style: solid; border-color: rgb(254, 254, 254); margin-top: 15px; padding: 2px; box-sizing: border-box; color: inherit; height: 12px; border-top-left-radius: 4px; border-top-right-radius: 4px; border-bottom-right-radius: 4px; border-bottom-left-radius: 4px;\" data-width=\"100%\"></section></section></section><section style=\"width: 0px; height: 0px; clear: both; border-top-left-radius: 4px; border-top-right-radius: 4px; border-bottom-right-radius: 4px; border-bottom-left-radius: 4px;\"></section></section><p><br/></p>', '1', '网格员');
