-- ----------------------------
-- Table structure for `wg_user`
-- ----------------------------
DROP TABLE IF EXISTS `wg_user`;
CREATE TABLE `wg_user` (
  `rid` int(11) NOT NULL AUTO_INCREMENT,
  `username` char(20) NOT NULL,
  `password` char(20) DEFAULT '111111',
  `clientId` int(11) DEFAULT '0',
  `departmentId` int(11) NOT NULL,
  `role` char(1) NOT NULL COMMENT 'T:网格员 W：相关工作人员 M:负责人 D:市区领导 P:警员 O:其他工作人员 L:其他领导 S:系统管理员',
  `title` char(20) DEFAULT NULL,
  `domainId` int(11) NOT NULL DEFAULT '1',
  `mobile` char(20) NOT NULL COMMENT '相关人员联系手机',
  `politics` char(10) DEFAULT '群众',
  `listOrder` int(5) DEFAULT '1',
  PRIMARY KEY (`rid`),
  KEY `department_index` (`departmentId`),
  KEY `client_index` (`clientId`)
) ENGINE=InnoDB AUTO_INCREMENT=1819 DEFAULT CHARSET=utf8;

