    this.chooseAndUploadImage=function(maxFiles,stepCb,completeCb){
        var _this=this;
        _this.chooseImage(function(localIds){
            if(localIds==null){
                completeCb(1);
            }else{
                if(localIds.length>maxFiles){
                    completeCb(2);
                }else{
                    async.eachSeries(localIds, function (localId,callback) {
                        _this.uploadImageWithUrl(localId,function(url){
                            stepCb(url);
                            callback();
                        });
                    }, function (err) {
                        completeCb(0);
                    });
                }
            }
        });
    }
