	hash = (!window.location.hash)?"Index":window.location.hash.replace('#frame','');
	window.location.hash = '#frame'+hash; 
	hashArr	=	new Array(hash);
	//-----------------------------		页面跳转
	window.onhashchange=function(){
		var	gun	=	gun==null?1:gun;
		forwards	=	'go';
		newhash	=	window.location.hash.replace('#frame','');
		newhash	=	!newhash?'Index':newhash;
		hashArr.push(newhash);
		hashL	=	hashArr.length;
		
		thispage	=	hashArr[hashL-1];
		tarpage		=	hashArr[hashL-2];
		thatpage	=	hashArr[hashL-3];
		if(hashL>1){
			if(thispage==thatpage){
				hashArr.pop();
				hashArr.pop();
				hashL	=	hashArr.length;
				forwards	=	'back';	
			}
		}
		if(forwards=='go'&&gun==1){
			gun	=	0;
			if(thispage=='Set'){
				S.destroyScroll();
				var newScroll	=	S.creatScroll('#frameIndex',{click:true,scrX:true,hash:'set',});
				x	=	newScroll.scrollerWidth*0.61;
				newScroll.scrollTo(x,0,300,newScroll.options.bounceEasing,'x');
				document.getElementById('setcover').style.display='block';
				
				S.destroyScroll();
				newc	=	'#frame'+thispage;
				var newScroll	=	S.creatScroll(newc,{click:true,scrX:true,hash:'lock'});
				setTimeout(function(){
					document.getElementById('setcoverl').style.display='block';
					document.getElementById('half').style.zIndex='-1';
				},300);
			}else{
				S.destroyScroll();
				newc	=	'frame'+thispage;
				document.getElementById(newc).style.display='block';
				if(thispage=='Msg'||thispage=='Itemshai'||thispage=='Itemtg'||thispage=='Qust'||thispage=='Buy'||thispage=='Redeal'||thispage=='Shoplist'||thispage=='Recharge'||thispage=='Rescore'||thispage=='Rescorezq'||thispage=='Rescorezr'){
					var newScroll	=	S.creatScroll('#'+newc,{click:true,scrX:true,pullToRefresh:true,onRefresh:function(){pull();},});
				}else{
					var newScroll	=	S.creatScroll('#'+newc,{click:true,scrX:true,});
				}
				function pull(){
					if(S.onRefresh!=null){
						S.onRefresh();
					}
					newScroll.refresh();
				}
				
				newScroll.scrollTo(newScroll.scrollerWidth,0,0,'','x');
				document.getElementById(newc).style.zIndex=hashL;
				setTimeout(function(){
					newScroll.scrollTo(0,0,300,newScroll.options.bounceEasing,'x');
				},1);
				// setTimeout(function(){
					// newScroll.refresh();
				// },800);	
			}
			gun=1;
		}else if(forwards=='back'&&gun==1){
			gun	=	0;
			if(tarpage=='Set'){
				S.destroyScroll();
				var newScroll	=	S.creatScroll('#frameIndex',{click:true,scrX:true,hash:'set',});
				newScroll.scrollTo(0,0,300,newScroll.options.bounceEasing,'x');
				document.getElementById('setcover').style.display='none';
				document.getElementById('half').style.zIndex='1';
				document.getElementById('setcoverl').style.display='none';
				setTimeout(function(){
					document.getElementById('half').style.zIndex='1';
					document.getElementById('setcoverl').style.display='none';
				},300);
				setTimeout(function(){
					newScroll.refresh();
				},800);
			}else{
				S.destroyScroll();
				newc	=	'frame'+tarpage;
				var newScroll	=	S.creatScroll('#'+newc,{click:true,scrX:true,});
				newScroll.scrollTo(newScroll.scrollerWidth,0,300,'','x');
				
				setTimeout(function(){
					newScroll.scrollTo(0,0,0,'','x');
					document.getElementById(newc).style.zIndex='-2';
					document.getElementById(newc).style.display='none';
				},300);
				S.destroyScroll();
				if(thispage=='Index'){
					var newScroll	=	S.creatScroll('#frameIndex',{click:true,scrX:true,hash:'set',});
				}else if(thispage=='Set'){
					var newScroll	=	S.creatScroll('#frameSet',{click:true,scrX:true,hash:'lock'});
				}else{
					var newScroll	=	S.creatScroll('#frame'+thispage,{click:true,scrX:true,});
					if(tarpage=='Buycheck'||tarpage=='Cart'||tarpage=='Order'){
						S.hideKeypad();
					}
				}
				// setTimeout(function(){
					// newScroll.refresh();
				// },800);
			}
			gun=1;
		}
	};
function ManagerRegister(){
    this.clientInfo={rid:0,clientNo:'',clientName:''};

    this.init=function(){
        var _this=this;
        appManager.register(function(client){
            if(client) {
                _this.clientInfo.rid=client.rid;
                _this.clientInfo.clientName=client.clientName;
                _this.clientInfo.clientNo=client.clientNo;
            }
        });
    }

    this.confirmRegister=function(){
        var _this=this;
        var companyId=getQueryString("companyId");
        ajaxGet('/weixin/managerRegister',{clientId:_this.clientInfo.rid,companyId:companyId},function(data){
            if(data.code==0){
                var url=appManager.currentDomainInfo.attachedUrl;
                if(!url){
                    url=appManager.convertUrlWithWeixinAuth('/app/app_crm_data/index.html');
                }else if(url.length<=7){
                    url=appManager.convertUrlWithWeixinAuth('/app/app_crm_data/index.html');
                }
                window.location.href=url;
            }
        });
    }
};
